package VariablesAndDataTypes;

public class VariablesAndDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Premitive datatypes are as follows:
		
		int age = 30;    //local variable
		char gender = 'M';
		boolean married = true;
		double xyz = 678.468;
		
		byte a=10;
		short b=40;
		long c=4999;
		float f = 566.543f;
		
		//String is not a datatype in java it is a class in Java
		
		String name= "Anjali";
		String username = "anjali1234";
		
		System.out.println(username);
		
		
		
		
		
		
		
		
	}

}
